import React, { useState } from 'react'
import axios from 'axios';
export default function CreateEmployee() {
    // const [name, setName] = useState("");
    // const [email, setEmail] = useState("");
    // const [mobNo, setMobNo] = useState("");
    // const [designation, setDesignation] = useState("");
    // const [gender, setGender] = useState("");
    // const [course, setCourse] = useState("");
    // const [photo, setPhoto] = useState("");
    // const [userName, setUserName] = useState("");

    const [formData,setFormData] = useState({
        userName:'',
        name:'',
        email:''
    })

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData((prevData) => ({
          ...prevData,
          [name]: value,
        }));
      };

      const handleSubmit = (e) => {
        e.preventDefault();
        axios.post('http://localhost:8000', formData)
          .then((response) => {
            console.log('Form data submitted:', response.data);
          })
          .catch((error) => {
            console.error('There was an error submitting the form!', error);
          });
      };
    return (
        <div>
            <link href="https://cdn.jsdelivr.net/npm/bootstrap@5.3.3/dist/css/bootstrap.min.css" rel="stylesheet" integrity="sha384-QWTKZyjpPEjISv5WaRU9OFeRpok6YctnYmDr5pNlyT2bRjXh0JMhjY6hW+ALEwIH" crossorigin="anonymous"/>
            <nav class="navbar navbar-expand-lg bg-body-tertiary">
                <div class="container-fluid">
                    <a class="navbar-brand" href="/dashboard">HOME</a>

                    <div class="collapse navbar-collapse" id="navbarText">
                    </div>
                </div>
            </nav>
            <form onSubmit={handleSubmit}>
                <label>User Name</label>
                <input type="text" name="userName" value={formData.userName} onChange={handleChange} /><br/>
                <label>Name</label>
                <input type="text" name="name" value={formData.name} onChange={handleChange} /><br/>
                <label>Email</label>
                <input type="email" name="email" value={formData.email} onChange={handleChange} /><br/>
                <label>Mob No</label>
                <input type="number" name="mobNo" value={formData.mobNo} onChange={handleChange} /><br/>
                <label>Designation</label>
                <input type="text" name="designation" value={formData.designation} onChange={handleChange} />
                <label>Gender</label>
                <input type="text" name="gender" value={formData.gender} onChange={handleChange} /><br/>
                <label>Course</label>
                <input type="text" name="course" value={formData.course} onChange={handleChange} /><br/>
                <label>Photo</label>
                <input type="file" name="photo" value={formData.photo} onChange={handleChange} /><br/>
                <button type="submit">Submit</button>
            </form>
        </div>
    )
}
