import React, { useState } from 'react'

export default function CreateEmployee() {
    const [name, setName] = useState("");
    const [email, setEmail] = useState("");
    const [mobNo, setMobNo] = useState("");
    const [designation, setDesignation] = useState("");
    const [gender, setGender] = useState("");
    const [course, setCourse] = useState("");
    const [photo, setPhoto] = useState("");

    const handleSubmit = (e) => {
        e.preventDefault();
        console.log(name);
      };
    return (
        <div>
            <form>
                <label>Name</label>
                <input 
                    type='text' 
                    name='name' 
                    value={name} 
                    onChange={(e) => 
                        setName(e.target.value)
                    }
                /><br/>
                <label>Email</label>
                <input 
                    type='text' 
                    name='email' 
                    value={email} 
                    onChange={(e) => 
                        setEmail(e.target.value)
                    }
                /><br/>
                <label>Mob No</label>
                <input 
                    type='text' 
                    name='mobNo' 
                    value={mobNo} 
                    onChange={(e) => 
                        setMobNo(e.target.value)
                    }
                /><br/>
                <label>Designation</label>
                <input 
                    type='text' 
                    name='designation' 
                    value={designation} 
                    onChange={(e) => 
                        setDesignation(e.target.value)
                    }
                /><br/>
                <label>Gender</label>
                <input 
                    type='text' 
                    name='gender' 
                    value={gender} 
                    onChange={(e) => 
                        setGender(e.target.value)
                    }
                /><br/>
                <label>Course</label>
                <input 
                    type='text' 
                    name='course' 
                    value={course} 
                    onChange={(e) => 
                        setCourse(e.target.value)
                    }
                /><br/>
                <label>Photo</label>
                <input 
                    type='file' 
                    name='photo' 
                    value={photo} 
                    onChange={(e) => 
                        setPhoto(e.target.value)
                    }
                /><br/>
                <button
                        type="submit"
                        value="Submit"
                        onClick={(e) => handleSubmit(e)}
                    >
                        Submit
                    </button>
            </form>
        </div>
    )
}
